exports.plugin = function(router, params) {
	
	for(var key in params) {
		process.env[key] = params[key];
	}
 

	router.on({
		
		'push validate/env/:name': function(query, mw) {
			if(!process.env[mw.request.params.name]) throw new Error('environment variable ' + name + ' does not exist');
		}
	});

};