module.exports = function() {

	
	var promotionFinders = [];
	
	//adds a service that can find promotion on a given item
	this.addPromotionFinder = function (promotionFinder) {

		promotionFinders.push(promotionFinder);
	}
	
	//returns a promotion finder based on the item. This runs through all the 
	//test functions
	this.getPromotionFinders = function (item) {

		var service = item.service,
			finders = [];
			
		
		//TODO - find promotion finder based on url. shared youtube link perhapsizzle?
		for(var i = promotionFinders.length; i--;) {

			var pf = promotionFinders[i];
			
			if(pf.test(item)) {

				finders.push(pf);
			}
		}

		
		return finders;
	}
}