
module.exports = function(db)
{
	var Schema = db.base.Schema,
	ObjectId = Schema.ObjectId;

	var CachedFeed = new Schema({
	
		group: String,
		feed: String,
		account: String,
		type: String, //reader, youtube
		feedId: String,
		auth: String, //google, twitter
		info: {}

	});

	return {
		CachedFeedModel: db.model('cache.feeds', CachedFeed),
		db: db
	};

}
