

module.exports = function(db)
{
	var Schema = db.base.Schema,
	ObjectId = Schema.ObjectId;

	var SpiceAppFeed = new Schema({
	
		//id of the feed
		feedId: String,

		//path to the group
		group: String,
		
		//search, wall, 
		action: String, 
		
		//flickr, twitter, facebook
		service: String, 

		//images, videos, default
		category: String,
		
		//url, query, etc.
		data: {}
	});

	return db.model('group.feeds', SpiceAppFeed); 
}

