exports.feedData = function(rawData)
{                            
	//note createdAt MUST be present, otherwise it will throw the cacher in an infinite loop.
	return {type:'article',
		    label:rawData.title, 
		    text:rawData.description,
		    createdAt:rawData.published, 
		    link:rawData.link
			,media:[{type:'url',link:rawData.link,match:rawData.link}]};
}