var net = require('psk/node/io/net'),
    loadJSON = net.loadJSON;

var Facebook = function(accessToken)
{
   var baseUrl = 'https://graph.facebook.com/';
   
   function fbload(uri,callback)
   {
     loadJSON(baseUrl+'/'+uri+'?access_token='+accessToken,callback);
   }
   
   this.getInfo = function(callback)
   {
     fbload('me',callback);
   }
}

exports.Facebook = Facebook;