var logger = require('mesh-winston').loggers.get('fig');

module.exports = function(view, res) {
	
	logger.debug('rendering view');
	
	view.setup({ el: window.document, $: $ }).init({
		complete: function() {

			logger.debug('finishing view');

			if(res) {
				res.header('dontPrint', true);
				res.end();
			}
		}
	});
}