var models = require('./models');

exports.plugin = function(router)
{
    router.on({

    	/**
    	 */

        'push init': function()
        {
            models.init();
        },                

        /**
         */

        'pull group/:publicNameOrId': function(req, res, mw)
        {
        	router.models.Group.findOne({ _id: req.query.publicNameOrId }, function(item)
			{
				req.group = item;

				mw.next();
			});
        }

    });
}