

module.exports = function(db)
{
    var Schema = db.base.Schema,
	ObjectId = Schema.ObjectId;
    
    var UserStorage = new Schema({
	
        //path to the group
        key: String,
        
        //the parent group
        value: {}, 
        
        //the owner of this group
        ttl: Number, 
        
        //name of group
        account: String
    });

	return {
		UserStorage: db.model('userStorage', UserStorage),
		db: db
	};
};

