
module.exports = function(db)
{
	var Schema = db.base.Schema,
	ObjectId = Schema.ObjectId;
	
	var Account = new Schema({

		_id: String,
		
		//the access token for the user so they can login
		profile		  : ObjectId
		
		//display name of account (what people see)
		, displayName : String
		
		//type of account
		, type		  : String
		
		//when the user joined
		, joined	  : Date
		
		//last time the user logged in
		, lastAccess  : Date
		
		//used to log the user in
		, secret 	  : {}
		
		//userid of the particular user on whatever site
		, userid	  : {}
		
		//additional data (email, user info) about the user
		, media	  : {} 

	});    

	return {
		Account: db.model('accounts', Account)                 
	}
}
