var redis = require('redis');


exports.params = {
	host: {type: 'input', name: 'Host'},
	port: {type: 'input', name: 'Port'},
	username: {type: 'input', name: 'Username'}, 
	password: {type: 'input', name: 'Password'}
	};
	
exports.plugin = function(mediator, params)
{
	var db = redis.createClient(params.port, params.host);
	
	function dbAuth()
	{
		db.auth(params.password);
	}
	
	db.addListener('connected', dbAuth);
	db.addListener('reconnected', dbAuth);
	dbAuth();
	
	
	
}